sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

// Get value from the user_name path parameter passed in the URL
var requestAsset = request.pathParams.asset_tag;	
// Get value of the ni_query query parameter passed in the URL
var requestAssetQuery = request.queryParams.asset_search;

// Query the sys_user table to get the user record for the user passed in
// the user_id path parameter.  
var requestAssetTag = new GlideRecord('alm_asset');
		//gs.info(requestAssetTag.getTableName());
requestAssetTag.get('asset_tag',requestAsset);									  	

//var requestAssetStatus = new GlideRecord('sys_choice');
		//gs.info(requestAssetTag.getTableName());
	//requestAssetStatus.get('label',requestAssetTag.install_status);	
	//requestAssetStatus.get('value','requestAssetTag.install_status');
//Create a body object.  Add property value pairs to the body.
var body = {};
body.asset = {"Asset Tag": requestAssetTag.asset_tag,
"Display Name": requestAssetTag.display_name,		  
"Asset SysID": requestAssetTag.sys_id,
"Asset Status": requestAssetTag.install_status,
"Serial Number": requestAssetTag.serial_number,
"Location": requestAssetTag.location.name,
"Department": requestAssetTag.department.name,
"Model Category": requestAssetTag.model_category.name,
"Model": requestAssetTag.model.name};	

// Send the response object, which is returned to the requestor, to the body object.
response.setBody(body);

})(request, response);
